import '@/styles/default.css'
import 'vue-loading-overlay/dist/vue-loading.css'
import AppHeader from '@/components/AppHeader.vue'
import AppFooter from '@/components/AppFooter.vue'
import Loading from 'vue-loading-overlay'
import * as loader from '@/helpers/loader.js'
export default {
  name: 'App',
  components: {
    AppHeader,
    AppFooter,
    Loading,
  },
  data() {
    return {
      scrolled: false,
      loading: false,
      announcements: [],
      joinus: [],
      research: [],
      members: [],
      tags: {},
      links: [],
      redirections: {},
    }
  },
  created() {
    this.loadData()
  },
  mounted() {
    window.addEventListener("scroll", this.handleScroll)
  },
  beforeDestroy() {
    window.removeEventListener("scroll", this.handleScroll)
  },
  methods: {
    async loadData() {
      this.loading = true
      const data = await loader.loadData()
      this.announcements = data.announcements
      this.joinus = data.joinus
      this.research = data.research
      this.members = data.members
      this.tags = data.tags
      this.links = data.links
      this.redirections = data.redirections
      this.handleRedirections()
      window.addEventListener('locationchange', this.handleRedirections)
      this.loading = false
    },
    handleRedirections() {
      const pathname = window.location.pathname
      const targetUrl = this.redirections[pathname]
      if (targetUrl) {
        this.loading = true
        window.location.href = targetUrl
      }
    },
    handleScroll() {
      const top = window.scrollY
      const threshold = 30
      if (!this.scrolled && top > threshold) {
        this.scrolled = true
      } else if (this.scrolled && top <= threshold) {
        this.scrolled = false
      }
    }
  }
}
